INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (1, 1,'La Divina Comedia',1320, 432, 'Espa√±ol', 'Italiano', 'La Divina Comedia es un poema √©pico escrito por Dante Alighieri. Es considerado uno de los grandes cl√°sicos de la literatura mundial y se divide en tres partes: Infierno, Purgatorio y Para√≠so.', 4, 'En medio del camino de nuestra vida me encontr√© en una selva oscura','/biblioweb/public/assets/img/covers/la-divina-comedia.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (1, 2,'Las Flores del Mal',1857,290,'Espa√±ol','Franc√©s','Las Flores del Mal es una colecci√≥n de poemas escritos por Charles Baudelaire. Fue muy controvertida en su √©poca debido a su tem√°tica er√≥tica y desafiante, pero hoy es considerada como una obra maestra literaria.',4,'Hip√≥crita lector, mi semejante, mi hermano!', '/biblioweb/public/assets/img/covers/las-flores-del-mal.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (1, 3,'Hoja de Hierba',1855,600,'Espa√±ol','Ingl√©s','Hoja de Hierba es una colecci√≥n de poemas escritos por Walt Whitman. Es considerada como uno de los grandes cl√°sicos de la poes√≠a estadounidense y ha sido muy influyente en la literatura del siglo XX.',4,'Yo celebro mi yo, Y lo que yo asumo, t√∫ tambi√©n asumir√°s.', '/biblioweb/public/assets/img/covers/hoja-de-hierba.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (1, 4,'Cien Sonetos de Amor',1959,120,'Espa√±ol','Espa√±ol','Cien Sonetos de Amor es una colecci√≥n de poemas escrita por el poeta chileno Pablo Neruda. Est√° compuesta por 100 sonetos que reflejan diferentes etapas del amor y ha sido muy influyente en la poes√≠a hispanohablante.',4,'Matilde, nombre de planta o piedra o vino, del fruto tal vez m√°s claro...','/biblioweb/public/assets/img/covers/cien-sonetos-de-amor.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (1, 5,'El Cuervo y otros poemas',1845,100,'Espa√±ol','Ingl√©s','El Cuervo y otros poemas es una colecci√≥n de obras po√©ticas escrita por Edgar Allan Poe. Incluye su famoso poema "El cuervo", as√≠ como otras piezas destacadas que reflejan la oscuridad, melancol√≠a y misticismo caracter√≠sticos del autor.',5,'Una vez, al filo de una l√∫gubre media noche...', '/biblioweb/public/assets/img/covers/el-cuervo-y-otros-poemas.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (1, 6,'Ariel',1965,81,'Espa√±ol','Ingl√©s','Ariel es una colecci√≥n de poes√≠a escrita por Sylvia Plath. Esta obra, publicada p√≥stumamente, incluye poemas intensos y emocionales que abordan temas como la muerte, el g√©nero y la identidad personal.',5,'Amoroso melocot√≥n reci√©n nacido entre las hojas...', '/biblioweb/public/assets/img/covers/ariel.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (1, 7,'Platero y yo',1914,200,'Espa√±ol','Espa√±ol','"Platero y yo" es una obra literaria escrita por Juan Ram√≥n Jim√©nez que narra las vivencias del autor junto a su burro Platero en Moguer, un pueblo andaluz. Aunque no es poes√≠a en s√≠, su estilo l√≠rico y prosa po√©tica le dan caracter√≠sticas similares.',5,'Platero es peque√±o..."', '/biblioweb/public/assets/img/covers/platero-y-yo.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (1, 4,'Odas elementales',1954,225,'Espa√±ol','Espa√±ol','"Odas elementales" es una colecci√≥n de poes√≠a escrita por Pablo Neruda. En ella, el autor rinde homenaje a objetos y elementos cotidianos, como la comida, la naturaleza y las relaciones humanas.',4,'Cebolla, luminosa redoma...','/biblioweb/public/assets/img/covers/odas-elementales.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (1, 8,'Altazor o el viaje en paraca√≠das',1931,160,'Espa√±ol','Espa√±ol','Altazor o el viaje en paraca√≠das" es un poema vanguardista escrito por Vicente Huidobro. La obra narra la ca√≠da del protagonista, Altazor, desde lo alto del cielo hasta su muerte, y es considerado uno de los hitos de la poes√≠a moderna.',4,'Voy a hundirme hasta la m√©dula del sue√±o. Dormir√© d√≠as y noches y al cabo me despertar√© cantando','/biblioweb/public/assets/img/covers/altazor.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (1, 9,'Sonetos del amor oscuro',1983, 64,'Espa√±ol','Espa√±ol','"Sonetos del amor oscuro" es una colecci√≥n de sonetos escrita por Federico Garc√≠a Lorca. Estos poemas, publicados p√≥stumamente, abordan temas como la pasi√≥n y el deseo.',5,'El poeta dice ser fiel a su lira y al fin se aleja...','/biblioweb/public/assets/img/covers/sonetos-del-amor-oscuro.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (2, 10, 'Alicia en el pa√≠s de las maravillas',1865,184,'Espa√±ol','Ingl√©s','La historia cuenta c√≥mo una ni√±a llamada Alicia cae por un agujero, encontr√°ndose en un mundo peculiar y extra√±o, poblado por humanos y criaturas antropom√≥rficas',5,'El Gato sonri√≥ al ver a Alicia. Parec√≠a tener buen car√°cter, consider√≥ Alicia; pero tambi√©n ten√≠a unas u√±as muy largas y un gran n√∫mero de dientes, de forma que pens√≥ que convendr√≠a tratarlo con el debido respeto.', '/biblioweb/public/assets/img/covers/alicia-en-el-pais-de-las-maravillas.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (2, 11, 'Anhelo',2020,672,'Espa√±ol','Ingl√©s','Una novela que aborda temas de deseo, amor, p√©rdida y autodescubrimiento. La trama sigue a los personajes principales mientras enfrentan sus propias luchas internas y externas en la b√∫squeda de sus deseos m√°s profundos. A medida que se desarrolla la historia, el lector es llevado en un viaje emocional donde las conexiones entre los personajes se entrelazan con desenlaces inesperados. El libro destaca c√≥mo las decisiones de cada individuo pueden afectar no solo su vida sino tambi√©n la de aquellos a su alrededor',4,'Estoy en la puerta que da a la pista de despegue mirando la avioneta en la que estoy a punto de subirme y esforz√°ndome al m√°ximo por no morirme del miedo. Es m√°s f√°cil decirlo que hacerlo.', '/biblioweb/public/assets/img/covers/anhelo.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (2, 12, 'Harry Potter y la piedra filosofal',1997,256,'Espa√±ol','Ingl√©s','Harry Potter se ha quedado hu√©rfano y vive en casa de sus abominables t√≠os y el insoportable primo Dudley. Harry se siente muy triste y solo, hasta que un buen d√≠a recibe una carta que cambiar√° su vida para siempre. En ella le comunican que ha sido aceptado como alumno en el Colegio Hogwarts de Magia',5,'No esperaba que sucediera as√≠, susurr√≥ Harry mientras miraban a toda prisa todo a su alrededor. ¬°Cierra la maldita puerta!, gru√±√≥ Ron, incapaz de creer lo que acababa de o√≠r. La se√±ora Norris estaba en el pasillo, ronroneando contenta.', '/biblioweb/public/assets/img/covers/harry-potter.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (2, 13, 'El Se√±or de los Anillos 1: La Comunidad del Anillo',1954,423,'Espa√±ol','Ingl√©s','En la adormecida e id√≠lica Comarca, un joven hobbit recibe un encargo: custodiar el Anillo √önico y emprender el viaje para su destrucci√≥n en las Grietas del Destino. Consciente de la importancia de su misi√≥n, Frodo abandona la Comarca e inicia el camino hacia Mordor con la compa√±√≠a inesperada de Sam, Pippin y Merry',3,'No hubo en el Anillo ning√∫n cambio aparente. Un momento despu√©s Gandalf se levant√≥, cerr√≥ los postigos y corri√≥ las cortinas. La habitaci√≥n se oscureci√≥, se hizo un silencio y se oy√≥ el ruido de las tijeras de Sam, ahora cerca de la ventana.', '/biblioweb/public/assets/img/covers/el-senior-de-los-anillos-comunidad-anillo.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (2, 14, 'Elantris',2005,496,'Espa√±ol','Ingl√©s','Anta√±o famosa sede de inmortales, un lugar repleto de poderosa magia, Elantris ha ca√≠do en desgracia. Ahora s√≥lo acoge a los nuevos muertos en vida, postrados en una insufrible no-vida tras una misteriosa y terrible transformaci√≥n',3,'Raoden yac√≠a en el suelo, respirando con dificultad mientras observaba las nubes flotar por encima. Su cuerpo adolorido no parec√≠a dispuesto a moverse, pero estaba vivo. Hab√≠a sobrevivido a la transformaci√≥n', '/biblioweb/public/assets/img/covers/elantris.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (2, 14, 'El camino de los reyes',2010,1200,'Espa√±ol','Ingl√©s','La trama sigue a varios personajes principales que se entrelazan a lo largo del libro, incluyendo al asesino Kaladin Stormblessed, quien lucha contra la desesperaci√≥n mientras lidia con su pasado como soldado; Shallan Davar, una joven noble cuyo objetivo es robar un artefacto m√≠stico para salvar a su familia empobrecida; y Dalinar Kholin, un alto pr√≠ncipe atormentado por visiones prof√©ticas',4,'Hizo a un lado la p√°gina. Algunos hombres coleccionaban trofeos. Otros coleccionaban armas o escudos. Muchos coleccionaban esferas. Shallan coleccionaba personas. Personas, y criaturas interesantes. Tal vez era debido a que hab√≠a pasado gran parte de su juventud en una prisi√≥n virtual', '/biblioweb/public/assets/img/covers/el-camino-de-los-reyes.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (2, 15, 'El nombre del viento',2007,873,'Espa√±ol','Ingl√©s','La historia sigue a Kvothe, un m√∫sico y mago talentoso que narra sus aventuras desde su infancia hasta su vida adulta mientras busca conocimiento, poder y venganza. A lo largo del relato, se entrelazan elementos como la m√∫sica, la magia llamada simpat√≠a, misteriosos seres llamados Chandrian y una escuela de artes arcanas conocida como la Universidad',2,'Si hubiera comido algo podr√≠a decir que era pasada la hora de comer. Estaba mendigando en la Rambla del Comercio; hasta ese momento hab√≠a conseguido dos patadas (de un guardia y de un mercenario), tres empujones (de dos carromateros y de un marinero)', '/biblioweb/public/assets/img/covers/el-nombre-del-viento.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (2, 16, 'Seis de Cuervos',2015,548,'Espa√±ol','Ingl√©s','Kaz Brekker, un genio del crimen que regenta un antro de juegos de azar y apuestas conocido como el Club Cuervo, debe reunir a un grupo de seis personas con las habilidades necesarias para entrar y salir de la Corte de Hielo, una fortaleza inexpugnable que mantiene bajo llave un secreto que podr√≠a dinamitar el equilibrio de poder en el mundo',5,'Nina hab√≠a estado en las afueras del grupo desde el principio. Matthias tambi√©n lo hab√≠a notado. Ella era gr√°cil como un gato y alta para una mujer; su cabello era una masa rizada color canela rojiza que apenas pod√≠a contenerse en su trenza despeinada', '/biblioweb/public/assets/img/covers/seis-de-cuervos.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (2, 17, 'Los jardines de la Luna',1999,776,'Espa√±ol','Ingl√©s','La novela detalla las diversas luchas por el poder en una regi√≥n intercontinental dominada por el imperio de Malazan. Es notable por el uso de la magia y su estructura inusual en la trama. Los jardines de la Luna se centra en la campa√±a imperial para conquistar la ciudad de Darujhistan en el continente de Genabackis',4,'Enfriadas estas cenizas, abrimos un antiguo libro. Sus p√°ginas, manchadas de aceite, narran las historias de los Ca√≠dos, del imperio en guerra, de palabras yermas. Mengua el fuego, su fulgor y las chispas de la vida no son sino recuerdos vistos por ojos entornados. Qu√© no suscitan en mi mente. Qu√© no dibujan mis pensamientos tras abrir el Libro de los Ca√≠dos, tras respirar el hondo aroma de la historia. Presta pues atenci√≥n a estas palabras llevadas en aquel aliento', '/biblioweb/public/assets/img/covers/los-jardines-de-la-luna.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (2, 18, 'Un mago de Terramar',1968,264,'Espa√±ol','Ingl√©s','La trama se ambienta en el archipi√©lago ficticio de Terramar, y se centra en un joven mago llamado Ged, que naci√≥ en la isla de Gont. Desde peque√±o da muestras de poseer un gran poder, y por eso asiste a una escuela de magia, donde su naturaleza conflictiva lo hace pelearse con uno de sus compa√±eros de estudio',3,'En el mundo de Terramar hay dragones y espectros, talismanes y poderes, y las leyes de la magia son tan inevitables y exactas como las leyes naturales. Un principio fundamental rige en ese mundo: el delicado equilibrio entre la muerte y la vida, que muy pocos hombres pueden alterar, o restaurar. Pues la restauraci√≥n del orden c√≥smico corresponde naturalmente al individuo que se gobierna a s√≠ mismo, el h√©roe completo capaz de dar el paso √∫ltimo, enfrentarse a su propia sombra, que es miedo, odio, inhumanidad', '/biblioweb/public/assets/img/covers/un-mago-de-terramar.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (3, 19, 'Bajo la misma estrella',2012,313,'Espa√±ol','Ingl√©s','Una historia que explora cu√°n exquisita y tr√°gica puede ser la aventura de saberse vivo y querer a alguien. Emotiva, ir√≥nica y afilada. Una novela te√±ida de humor y de tragedia que habla de nuestra capacidad para so√±ar incluso en las circunstancias m√°s dif√≠ciles. A Hazel y a Gus les gustar√≠a tener vidas m√°s corrientes',5,'A finales del invierno de mi decimos√©ptimo a√±o de vida, mi madre lleg√≥ a la conclusi√≥n de que estaba deprimida, seguramente porque apenas sal√≠a de casa, pasaba mucho tiempo en la cama, le√≠a el mismo libro una y otra vez, casi nunca com√≠a  y dedicaba buena parte de mi abundante tiempo libre a pensar en la muerte', '/biblioweb/public/assets/img/covers/bajo-la-misma-estrella.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (3, 20, 'Yo antes de ti',2012,369,'Espa√±ol','Ingl√©s','Re√∫ne a dos personas que no podr√≠an tener menos en com√∫n en una novela conmovedoramente rom√°ntica con una pregunta: ¬øQu√© decidir√≠as cuando hacer feliz a la persona a la que amas significa tambi√©n destrozarte el coraz√≥n?',5,'Cuando √©l sale del ba√±o ella est√° despierta, recostada contra las almohadas, hojeando los folletos de viaje que hab√≠a junto a la cama. Viste una de las camisetas de √©l y su larga melena enmara√±ada evoca im√°genes de la noche anterior. √âl se queda ah√≠, disfrutando de ese breve recuerdo, mientras se seca el pelo con una toalla', '/biblioweb/public/assets/img/covers/yo-antes-de-ti.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (3, 21, 'La Selecci√≥n',2012,327,'Espa√±ol','Ingl√©s','Para treinta y cinco chicas, La Selecci√≥n es una oportunidad que s√≥lo se presenta una vez en la vida. La oportunidad de escapar de la vida que les ha tocado por nacer en una determinada familia. La oportunidad de que las trasladen a un mundo de trajes preciosos y joyas que no tienen precio',5,'Aquellos ojos verdes escrutaron el interior de mi alma, y sent√≠ aquella chispa que nos un√≠a y que no hab√≠a compartido con nadie m√°s. Yo tambi√©n quer√≠a nuestro futuro feliz.', '/biblioweb/public/assets/img/covers/la-seleccion.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (3, 22, 'Una corte de rosas y espinas',2015,432,'Espa√±ol','Ingl√©s','Cuando la cazadora de diecinueve a√±os Feyre mata a un lobo en el bosque, una criatura bestial irrumpe en su casa para exigir una retribuci√≥n. Trasladada a una tierra m√°gica y enga√±osa que solo conoce a trav√©s de leyendas, Feyre descubre que su captor no es un animal, sino Tamlin: una criatura inmortal y letal que alguna vez rein√≥ en su mundo.',5 ,'El bosque se hab√≠a transformado en un liberito de hielo y nieve. Yo hab√≠a estado monitoreando los alrededores de l sotobosque durante una hora, y mi punto de observaci√≥n, en la horqueta de una rama, se hab√≠a convertido en una atalaya in√∫til. El viento soplaba en r√°fagas espesas que borraban mis huellas.', '/biblioweb/public/assets/img/covers/corte-de-rosas-y-espinas.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (3, 23, 'El proyecto esposa',2013,329,'Espa√±ol','Ingl√©s','Tiene un protagonista excepcional: el inteligente, obsesivo y literal Don Tillman, para quien navegar por este mundo de emociones complejas e incoherentes, poblado por seres que a menudo dicen exactamente lo contrario de lo que piensan, constituye una tarea tan ardua como desconcertante',5,'Era una declaraci√≥n sumamente extraordinaria. Y de una l√≥gica aplastante. Yo hab√≠a dado por supuesto que el amor rom√°ntico siempre ser√≠a algo ajeno al √°mbito de mi experiencia, pero explicaba a la perfecci√≥n mi situaci√≥n actual. Quise asegurarme', '/biblioweb/public/assets/img/covers/el-proyecto-esposa.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (3, 24, 'El amor en los tiempos del c√≥lera',2012,313,'Espa√±ol','Espa√±ol','Relata la historia de amor entre Fermina Daza y Florentino Ariza. Un amor que tendr√° que enfrentarse al tiempo pero que no morir√°',5,'No le dijo a nadie que se iba, no se despidi√≥ de nadie, con el hermetismo f√©rreo con que s√≥lo le revel√≥ a la madre el secreto de su pasi√≥n reprimida, pero a la v√≠spera del viaje cometi√≥ a conciencia una locura √∫ltima del coraz√≥n que bien pudo costarle la vida', '/biblioweb/public/assets/img/covers/el-amor-en-tiempos-de-colera.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (3, 25, 'Cincuenta sombras de Grey',2011,530,'Espa√±ol','Ingl√©s','Narra la historia de Anastasia Steele, una joven estudiante, que cursa la carrera de Literatura en la Universidad de Washington, y que vive con su mejor amiga, Katherine Kavanagh, quien escribe para el peri√≥dico estudiantil de su universidad. Debido a un resfriado, Katherine persuade a Ana para que tome su lugar en la entrevista que le har√≠a a Christian Grey, un joven empresario de veintisiete a√±os, apuesto y exitoso',5,'Estuve dando vueltas a diversas opciones. Estoy muy confundida. La idea que tiene Christian de una relaci√≥n se parece mucho a una oferta de empleo, con sus horarios, la descripci√≥n del trabajo y un procedimiento de resoluci√≥n de conflictos bastante riguroso. No imaginaba as√≠ mi primera historia de amor', '/biblioweb/public/assets/img/covers/cincuenta-sombras-de-grey.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (3, 26, 'Donde termina el arco iris',2004,512,'Espa√±ol','Ingl√©s','Nos presenta una selecci√≥n de los divertidos y frescos di√°logos que Rosie y Alex intercambian, y que nos llevan a conocer en profundidad sus respectivas vidas: sus complicados matrimonios y los esfuerzos por hacer sus sue√±os realidad',5,'Me alegr√≥ mucho verte. ¬°Te vi fant√°stica! ¬°Y NO est√°s gorda! La peque√±a Katie no se mostr√≥ muy habladora, pero ya estoy perdidamente enamorado de ella. Hasta se me ocurri√≥ rob√°rtela y llev√°rmela conmigo de vuelta a Boston', '/biblioweb/public/assets/img/covers/donde-termina-el-arcoiris.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (3, 27, 'Un paseo para recordar',1999,240,'Espa√±ol','Ingl√©s','La historia de dos adolescentes que se enamoran a pesar de la disparidad de sus alusiones personales. Jamie era la √∫ltima persona de la que se enamorar√≠a Landon. Seria y conservadora, era lo opuesto a una chica "cool", pero a ella tampoco le importaba; hija de un pastor de la iglesia baptista de la ciudad, Jamie no ten√≠a miedo en hacer saber a la gente que su fe era lo m√°s importante en su vida, aunque ello le costara algunos amigos',5,'Estar enamorado de una ni√±a como Jamie Sullivan era sin una duda la cosa m√°s extra√±a que alguna vez me hab√≠a pasado. No s√≥lo por que era una ni√±a en la que nunca hab√≠a pensado antes de ese a√±o -aunque hab√≠amos crecido juntos- pero hab√≠a algo diferente en la manera en que mis sentimientos hacia ella se desarrollaron', '/biblioweb/public/assets/img/covers/un-paseo-para-recordar.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (3, 27, 'Diario De Una Pasion',2004,236,'Espa√±ol','Ingl√©s','Es la historia de Noah Calhoun y Allie Hamilton, dos j√≥venes adolescentes de Carolina del Norte que, a pesar de vivir en dos ambientes sociales muy diferentes, se enamoraron profundamente y pasaron juntos un verano inolvidable, antes de ser separados, primero por sus padres, y m√°s tarde por la guerra',5,'Y a pesar de que no pod√≠a marcar el momento exacto -el d√≠a anterior despu√©s de la cena, o esa misma tarde en la canoa, o cuando hab√≠an visto los cisnes, o quiz√°s incluso en ese momento, mientras caminaban de la mano- Allie supo que se hab√≠a vuelto a enamorar de Noah Taylor Calhoun, nunca hab√≠a dejado de estar enamorada de √©l', '/biblioweb/public/assets/img/covers/diario-de-una-pasion.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (4, 28, 'Salvaje',2022,42,'Castellano','Castellano','Salvaje es el brillante debut de la autora hawaiana Emily Hughes. Con frescura y desparpajo nos cuenta la historia de una ni√±a que vive feliz en la naturaleza, donde los osos le han ense√±ado a comer, los p√°jaros a hablar y los zorros a jugar. Ella es audaz, valiente e incre√≠blemente libre. Pero un d√≠a un nuevo animal entra en escena, un animal extra√±amente parecido a ella.',5,'Nadie recordaba c√≥mo hab√≠a llegado la ni√±a, pero a todos les pareci√≥ bien. El bosque entero la acept√≥ como una m√°s. P√°jaro le ense√±√≥ a hablar. Oso le ense√±√≥ a comer. Zorro le ense√±√≥ a jugar...', '/biblioweb/public/assets/img/covers/salvaje.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (4, 29,  'Mujeres', 2020, 120, 'Espa√±ol', 'Espa√±ol', 'Mujeres es un libro escrito por Isabel Ruiz Ruiz. Presenta la vida de diversas mujeres destacadas en la historia, desde cient√≠ficas hasta artistas y l√≠deres pol√≠ticas. A trav√©s de sus historias, el libro busca inspirar a las j√≥venes y mostrarles el potencial que tienen para cambiar el mundo.', 5, 'No hay barrera, cerradura ni cerrojo que puedas imponer a la libertad de mi mente', '/biblioweb/public/assets/img/covers/mujeres.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (4, 30,  'Las princesas tambi√©n se tiran pedos', 2018, 32, 'Espa√±ol', 'Portugu√©s', 'Las princesas tambi√©n se tiran pedos es un libro escrito por Ilan Brenman y Ionit Zilberman. De manera divertida y rompiendo estereotipos, el libro muestra que incluso las princesas tienen comportamientos normales como cualquier otra persona.', 4, 'Laura se levant√≥ y empez√≥ a pasar los dedos por el lomo de los libros, cuando, de repente, una motita de polvo cay√≥ sobre la punta de su nariz. Junt√≥ los ojos intentando observarla y, de un soplido, la hizo volver a subir.', '/biblioweb/public/assets/img/covers/las-princesas-tambien-pedos.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (4, 31,  'Malena Ballena', 2015, 36, 'Espa√±ol', 'Italiano', 'Malena Ballena es un libro escrito por Davide Cali y Sonja Bougaeva. Cuenta la historia de Malena, una ballena que se siente triste y excluida debido a su tama√±o. A medida que conoce a otros animales, descubre la importancia de la amistad y la aceptaci√≥n.', 4, 'Malena siempre se las arreglaba para ser la √∫ltima en meterse en la piscina. Porque, cada vez que se tiraba al agua, provocaba una enorme ola y muchas voces gritaban: ¬°Malena es una ballena!', '/biblioweb/public/assets/img/covers/malena-ballena.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (4, 32,  'La princesa vestida con una bolsa de papel', 1992, 32, 'Espa√±ol', 'Ingl√©s', 'La princesa vestida con una bolsa de papel es un libro escrito por Robert Munsch y Michael Martchenko. Cuenta la historia de una princesa poco convencional que prefiere vestirse con una bolsa de papel en lugar de usar ropa elegante. El libro ense√±a lecciones de aceptaci√≥n y autoestima.', 4, 'En un reino encantado, una princesa singular desaf√≠a las convenciones al vestirse con una bolsa de papel. Su autenticidad y valent√≠a la llevan a enfrentar peligros y romper encantamientos, demostrando que la verdadera belleza trasciende las apariencias superficiales. Un cuento inspirador de amor y valent√≠a.', '/biblioweb/public/assets/img/covers/la-princesa-bolsa-papel.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (4, 33,  'Monstruo rosa', 2013, 36, 'Espa√±ol', 'Espa√±ol', 'Monstruo rosa es un libro escrito por Olga de Dios. Cuenta la historia de un monstruo que nace en un mundo en blanco y negro y se siente diferente a los dem√°s. A medida que explora su entorno, descubre la diversidad y la importancia de ser uno mismo.', '5', 'Elizabeth era una princesa muy linda. Viv√≠a en un castillo y ten√≠a lujosos vestidos de princesa. Se iba a casar con un pr√≠ncipe llamado Ronaldo.', '/biblioweb/public/assets/img/covers/monstruo-rosa.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (4, 34,  'Las jirafas no pueden bailar', 1999, 32, 'Espa√±ol', 'Ingl√©s', 'Las jirafas no pueden bailar es un libro escrito por Giles Andreae y Guy Parker-Rees. Narra la historia de una jirafa llamada Gerald que sue√±a con bailar, pero se siente desanimada por sus largas y torpes piernas. A trav√©s de la ayuda de un amigo, Gerald descubre que todos tienen su propio ritmo y talento.', 3, 'Con la cabeza agachada y arrastrando las pezu√±as, chufa se volvi√≥ a casa m√°s triste y sola que nunca', '/biblioweb/public/assets/img/covers/jirafas-no-bailar.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (4, 35,  'La princesa Li', 2021, 40, 'Espa√±ol', 'Espa√±ol', 'La princesa Li es un libro escrito por Elena Rendeiro y Luis Amavisca. Cuenta la historia de la princesa Li, quien decide enfrentarse a los estereotipos de g√©nero y vivir su vida seg√∫n sus propios t√©rminos. El libro promueve la igualdad de g√©nero y la libertad de elecci√≥n.', 5, 'La princesa Li viv√≠a con su padre, el rey Wan Tan, en un hermoso palacio... ella amaba a Beatriz, una chica de una tierra lejana. Las dos eran muy felices hasta que Wan Tan mand√≥ llamar a la princesa: ¬´Hab√≠a llegado el d√≠a de casarse con un joven de la corte¬ª.', '/biblioweb/public/assets/img/covers/la-princesa-li.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (4, 36,  'Orejas de mariposa', 2006, 28, 'Espa√±ol', 'Portugu√©s', 'Orejas de mariposa es un libro escrito por Luisa Aguilar y Andr√© Neves. Narra la historia de una ni√±a llamada Luc√≠a que se siente avergonzada por tener las orejas grandes. A trav√©s de diversas situaciones, Luc√≠a aprende a aceptarse a s√≠ misma y a valorar su singularidad.', 4, 'Mam√°, ¬øt√∫ crees que soy una orejotas?¬ª. <<No, hija. Tienes orejas de mariposa¬ª. Pero, ¬øc√≥mo son las orejas de mariposa?', '/biblioweb/public/assets/img/covers/orejas-de-mariposa.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (4, 37,  'Mercedes quiere ser bombera', 2019, 32, 'Espa√±ol', 'Espa√±ol', 'Mercedes quiere ser bombera es un libro escrito por Beatriz Monc√≥ y Mabel Pi√©rola. Cuenta la historia de Mercedes, una ni√±a que desaf√≠a los estereotipos de g√©nero al so√±ar con convertirse en bombera. El libro inspira a las ni√±as a perseguir sus sue√±os y a creer en s√≠ mismas.', 4, 'Mercedes y Miguel son amigos. Cada ma√±ana van juntos al colegio, por el camino hablan de muchas cosas y se lo pasan muy bien. En el patio les esperan Isabel y Javier, dos hermanos mellizos, casi iguales, a quienes les gusta compartir la ropa y los juguetes', '/biblioweb/public/assets/img/covers/mercedes-quiere-ser-bombera.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (6, 38,  'La Isla Misteriosa', 1874, 450, 'Espa√±ol', 'Franc√©s', 'Un grupo de n√°ufragos lucha por sobrevivir en una isla desconocida llena de misterios y peligros.', 4, 'Los supervivientes exploraron la playa en busca de recursos.', '/biblioweb/public/assets/img/covers/la-isla-misteriosa.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (6, 39,  'El Oro de los Dioses', 1987, 320, 'Espa√±ol', 'Ingl√©s', 'Un arque√≥logo se embarca en una aventura para encontrar una antigua ciudad perdida llena de tesoros.', 3, 'El arque√≥logo descendi√≥ por las escaleras antiguas, iluminado solo por una antorcha.', '/biblioweb/public/assets/img/covers/el-oro-de-los-dioses.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (6, 52,  'El Tesoro del Pirata', 2005, 280, 'Espa√±ol', 'Espa√±ol', 'Un joven marinero se enfrenta a peligrosos piratas en su b√∫squeda por encontrar un legendario tesoro oculto.', 4, 'Con su espada en mano, el joven se lanz√≥ al abordaje del barco pirata.', '/biblioweb/public/assets/img/covers/el-tesoro-del-pirata.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (6, 40,  'La Monta√±a Prohibida', 2010, 400, 'Espa√±ol', 'Ingl√©s', 'Un alpinista intr√©pido se enfrenta a los desaf√≠os de escalar una monta√±a maldita, llena de secretos y peligros mortales.', 5, 'El alpinista asegur√≥ su cuerda y comenz√≥ a ascender por la empinada pared de roca.', '/biblioweb/public/assets/img/covers/la-montania-prohibida.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (6, 41,  'El Cetro Perdido', 1999, 350, 'Espa√±ol', 'Franc√©s', 'Un aventurero valiente se embarca en una misi√≥n para encontrar un cetro m√°gico que otorga poderes sobrenaturales.', 4, 'El aventurero atraves√≥ la selva espesa con su machete en mano.', '/biblioweb/public/assets/img/covers/el-cetro-perdido.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (6, 42,  'El Enigma de la Pir√°mide', 2003, 420, 'Espa√±ol', 'Ingl√©s', 'Un grupo de arque√≥logos descubre un enigma oculto dentro de una antigua pir√°mide egipcia.', 3, 'Los arque√≥logos resolvieron el enigma y una puerta secreta se abri√≥ lentamente.', '/biblioweb/public/assets/img/covers/el-enigma-de-la-piramide.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (6, 43,  'La Espada del Destino', 2015, 380, 'Espa√±ol', 'Polaco', 'Un guerrero legendario se embarca en una b√∫squeda para encontrar una espada m√°gica que puede cambiar el curso de la historia.', 5, 'El guerrero desenvain√≥ la espada y su brillo ilumin√≥ la oscuridad.', '/biblioweb/public/assets/img/covers/la-espada-del-destino.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (6, 44,  'El Misterio de la Ciudad Perdida', 2008, 300, 'Espa√±ol', 'Ingl√©s', 'Un explorador audaz se aventura en una selva peligrosa en busca de una ciudad antigua desaparecida hace siglos.', 4, 'El explorador sigui√≥ las pistas hasta llegar a las ruinas cubiertas de vegetaci√≥n.', '/biblioweb/public/assets/img/covers/el-misterio-ciudad-perdida.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (6, 45,  'La Odisea del Navegante', 2012, 360, 'Espa√±ol', 'Espa√±ol', 'Un navegante solitario se embarca en una odisea √©pica a trav√©s de los oc√©anos, enfrentando tormentas y desaf√≠os mortales.', 4, 'El navegante se mantuvo firme mientras las olas golpeaban su barco.', '/biblioweb/public/assets/img/covers/la-odisea-del-navegante.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (6, 46,  'El Secreto de la Isla', 2006, 400, 'Espa√±ol', 'Ingl√©s', 'Un grupo de exploradores descubre un secreto oculto en una isla remota, desatando una serie de eventos extraordinarios.', 3, 'Los exploradores se adentraron en la jungla, sin saber lo que encontrar√≠an.', '/biblioweb/public/assets/img/covers/el-secreto-de-la-isla.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (7, 47, 'Lo √∫nico', 2015, 329,'Espa√±ol','Ingl√©s','¬øHas pensado en dividirte para poder llegar a solucionar todos aquellos asuntos de tu vida que te apremian? Tienes que saber que no ser√≠a una buena idea. Vivimos en un aluvi√≥n diario de correos electr√≥nicos, tweets, y reuniones que demandan nuestra atenci√≥n. Esto nos lleva a resultados mediocres, incumplimiento de plazos, menos dinero y m√°s preocupaciones.¬øQuieres ser m√°s productivo en tu trabajo? ¬øQuieres mejorar tu estilo de vida? En Lo √∫nico Gary Keller y Jay Papasan te muestran que el proceso es bastante sencillo.',4,'La clave del √©xito radica en enfocarse en una sola cosa. La concentraci√≥n nos permite alcanzar resultados extraordinarios. Evita la dispersi√≥n y dirige tu energ√≠a hacia lo que realmente importa. Identifica tu \'√∫nica cosa\' y trabaja con determinaci√≥n y disciplina para lograrla.', '/biblioweb/public/assets/lo-unico.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (7, 48, 'Poder Sin L√≠mites', 1996, 428,'Espa√±ol','Ingl√©s','Esta sencilla m√°xima muestra elocuentemente la teor√≠a b√°sica de este libro: el problema esencial del desarrollo personal no est√° en las circunstancias, sino en nuestra actitud vital. Solemos pedirle a la vida un precio bajo, limitando nuestras ambiciones, siendo presas de la frustraci√≥n o el miedo de ir m√°s all√°. Y esta actitud negativa es lo que Poder sin l√≠mites ayuda a combatir ense√±√°ndonos el poder oculto del cerebro, los mecanismos correctos de relaci√≥n interpersonal e incluso h√°bitos alimentarios adecuados.',5,'El √©xito en la vida no est√° determinado por las circunstancias externas, sino por las respuestas internas que elegimos dar. Nuestra actitud, nuestras creencias y nuestros patrones mentales moldean nuestro destino. Al tomar el control de nuestras emociones y enfocarnos en nuestras metas, podemos desatar nuestro verdadero potencial.', '/biblioweb/public/assets/poder-sin-limites.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (7, 49, 'Roba como un artista', 2012, 144,'Espa√±ol','Ingl√©s','Nada es original, dice el autor, as√≠ que mejor acepta las influencias, instr√∫yete en el trabajo de los dem√°s, reimagina y mezcla tu propio camino. Encuentra un pasatiempo que ames y convi√©rtelo en tu trabajo: escribe el libro que te gustar√≠a leer y la pel√≠cula que te gustar√≠a ver. Y pues, ya sabes: no te endeudes, come sano, act√∫a con sentido com√∫n, ¬°y atr√©vete a ser aventado y osado!',4,'El robo es la base de todo arte. Todo artista se apropia, mezcla y remezcla ideas para crear algo nuevo. No se trata de copiar literalmente, sino de tomar inspiraci√≥n de otros y agregarle tu propio toque personal. El arte es un collage interminable de influencias, y nuestra tarea es encontrar nuestra voz √∫nica en medio de ese caos creativo.', '/biblioweb/public/assets/roba-como-un-artista.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (7, 50, 'Deep Work', 2016, 304,'Ingl√©s','Ingl√©s','The ability to do deep work. Cal Newport\'s exciting new book is an introduction and guide to the kind of intense concentration in a distraction-free environment that results in fast, powerful learning and performance. Think of it as calisthenics for your mind-and start your exercise program today',5,'Deep work is the ability to focus without distraction on a cognitively demanding task. In a world filled with interruptions and attention fragmentation, mastering deep work has become more valuable than ever. It\'s the key to producing high-value results and achieving success in an increasingly competitive world.', '/biblioweb/public/assets/deep-work.jpg');
INSERT INTO `libro`(`id_genero`, `id_autor`, `titulo`, `anio_publicacion`, `num_paginas`, `idioma`, `idioma_original`, `descripcion`, `rating`, `fragmento`, `portada`) VALUES (7, 51, 'Originales', 2016, 352,'Espa√±ol','Ingl√©s','¬øC√≥mo generar nuevas ideas sin temer romper con lo establecido? ¬øDe qu√© forma defender iniciativas que van en contra de la corriente, oponerse a la conformidad y a la tradici√≥n? A partir de casos concretos, Adam Grant explora qu√© ocurri√≥ detr√°s de situaciones realmente disruptivas que cambiaron en algo el curso de un proyecto, una empresa o una cultura entera. Nos ense√±a c√≥mo reconocer una idea buena y, ante todo, la persistencia que se necesita para convertirla en realidad cuando se trata de algo que contraviene lo aceptado por la mayor√≠a.',5,'Los originales son aquellos que desaf√≠an el statu quo y generan ideas frescas. No temen al rechazo y persisten a pesar de la adversidad. La clave para ser un original radica en tomar riesgos calculados, buscar m√∫ltiples perspectivas y mantener una mentalidad abierta. Son aquellos que cambian el mundo con sus ideas y acciones audaces.', '/biblioweb/public/assets/originales.jpg');
